import React from 'react';
import './task2.css';
class Task2 extends React.Component{
    constructor(){
        super()
        this.state={message:'',message1:'',message2:'',message3:''}
    } 
    change(){
        this.setState({message:"Hello"})
    }
    change1(){
        this.setState({message1:"Hello"})
    }
    change2(){
        this.setState({message2:"Hello"})
    }
    change3(){
        this.setState({message3:"Hello"})
    }
    render(){
        return(
            <div className='item'>
                <button onClick={()=>this.change()}>Item1<h1>{this.state.message}</h1></button>
                <button onClick={()=>this.change1()}>Item2<h1>{this.state.message1}</h1></button>
                <button onClick={()=>this.change2()}>Item3<h1>{this.state.message2}</h1></button>
                <button onClick={()=>this.change3()}>Item4<h1>{this.state.message3}</h1></button>
                <div>
                    <button>Count</button>
                </div>
            </div>
        )
    }
}
export default Task2;
