import React,{Component} from "react";
export class Lifecycle1 extends Component{
    constructor(){
        super()
        this.state={inr:0,usd:0,eur:0,aus:0,afn:0,lkr:0,krw:0}
    }

inrCh=(event)=>{
    this.setState({
        inr:event.target.value,
        usd:event.target.value*82.09

    })
}
usdCh=(event)=>{
    this.setState({
        usd:event.target.value,
        inr:event.target.value/82.09
    })
}
eurCh=(event)=>{
    this.setState({
        eur:event.target.value,
        aus:event.target.value*1.644,
        lkr:event.target.value*360.905,
        afn:event.target.value*95.379,
        krw:event.target.value*1428.597
    })
}
ausCh=(event)=>{
    this.setState({
        eur:event.target.value / 1.644,
        aus:event.target.value,
        lkr:event.target.value *219.590,
        afn:event.target.value *58.033,
        krw:event.target.value *869.219
    })
}
lkrCh=(event)=>{
    this.setState({
        eur:event.target.value /360.905,
        aus:event.target.value/219.590,
        lkr:event.target.value,
        afn:event.target.value * 0.264,
        krw:event.target.value*3.958
    })
}
afnCh=(event)=>{
    this.setState({
        eur:event.target.value/95.379,
        aus:event.target.value/58.033,
        lkr:event.target.value/0.264,
        afn:event.target.value ,
        krw:event.target.value*14.978
    })
}
krwCh=(event)=>{
    this.setState({
        eur:event.target.value/1428.597,
        aus:event.target.value/869.219,
        lkr:event.target.value /3.958 ,
        afn:event.target.value / 14.978,
        krw:event.target.value
    })
}

render(){
    const {inr,usd,eur,aus,afn,lkr,krw}=this.state
    return(
        <div>
            <label>INR </label>
            <input value={inr} onChange={this.inrCh}/><br></br>
            <label>USD </label>
            <input value={usd} onChange={this.usdCh}/><br></br><br></br><br></br>
            <label>EUR</label>
            <input value={eur} onChange={this.eurCh}/><br></br>
            <label>Aus</label>
            <input value={aus} onChange={this.ausCh}/><br></br>
            <label>AFN</label>
            <input value={afn} onChange={this.afnCh}/><br></br>
            <label>LKR</label>
            <input value={lkr} onChange={this.lkrCh}/><br></br>
            <label>KRW</label>
            <input value={krw} onChange={this.krwCh}/><br></br>
            
        </div>
    )
}
}
export default Lifecycle1;
