import React from'react';
export class Form extends React.Component{
    constructor(props){
        super(props)
        this.state={username:'',address:'',job:'developer'}
    }
    userchange=(event)=>{
        this.setState({username:event.target.value})
    }
    addchange=(event)=>{
        this.setState({address:event.target.value})
    }
    jobchange=(event)=>{
        this.setState({job:event.target.value})
    }
    submit=(event)=>{
        console.log(this.state.username)
        console.log(this.state.address)
        console.log(this.state.job)
        event.preventDefault()
    }
    render(){
        return(
            <div>
                <form onSubmit={this.submit}>
                    <label>Username</label>
                    <input type='text' value={this.state.username} onChange={this.userchange}/>
                    <br></br>
                    <label>Address</label>
                    <textarea value={this.state.address} onChange={this.addchange}></textarea>
                    <br></br>
                    <label>Job</label>
                    <select value={this.state.job} onChange={this.jobchange}>
                        <option value="developer">developer</option>
                        <option value="manager">manager</option>
                        <option value="qa">QA</option>
                    </select>
                    <br></br>
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}
export default Form;
