import React, { Component } from "react";
import Child1 from './child1';
class Task7 extends Component{
    constructor(props){
        super(props)
        this.state={
            counttab:0
        }
    }
    open=(expand)=>{
        if(expand){
            this.setState(prev=>({counttab:prev.counttab-1}))
        }
        else{
            this.setState(prev=>({counttab:prev.counttab+1}))
        }
    }
    render()
    {
        return(
            <div>
                <Child1 title='Item1' content='content for item1' open={this.open}/>
                <Child1 title='Item2' content='content for item2' open={this.open}/>
                <Child1 title='Item3' content='content for item3' open={this.open}/>
                <h1>{this.state.counttab}opened</h1>
            </div>
        )
    }

}
export default Task7;
