import React from 'react';
import './task2.css';
class Task2 extends React.Component{
    constructor(){
        super()
        this.state={message:'',message1:'',message2:'',message3:''}
    } 
    change(){
        this.setState({message:"Hello",message1:"Hello",message2:"Hello",message3:"Hello"})
    }
    
    render(){
        return(
            <div>
                <button className="button1"  onClick={()=>this.change()}>Item1<h1>{this.state.message}</h1></button>
                <button onClick={()=>this.change()}>Item2<h1>{this.state.message1}</h1></button>
                <button onClick={()=>this.change()}>Item3<h1>{this.state.message2}</h1></button>
                <button onClick={()=>this.change()}>Item4<h1>{this.state.message3}</h1></button>
            </div>
        )
    }
}
export default Task2;
