import React from "react";
const conversion={
    inr:{usd:0.0122,aus:0.0178,uk:0.0245,riyal:0.0034,inr:1},
    usd:{usd:1,aus:1.46,uk:0.77,riyal:3.75,inr:82.09},
    aus:{usd:0.6831,aus:1,uk:0.49,riyal:2.56,inr:56.05},
    uk:{usd:1.3,aus:2.03,uk:1,riyal:4.20,inr:91.93},
    riyal:{usd:0.27,aus:0.39,uk:0.24,riyal:1,inr:21.81}
}
export class Task10 extends React.Component{
    constructor(props){
        super(props)
        this.state={inp1:"inr",inp2:"inr",inp1box1:0,inp2box2:0}
    }
    inp1boxChn=(event)=>{
        const {inp1,inp2}=this.state
        const price=conversion[inp1][inp2]
        this.setState=({inp1box1:event.target.value,
                        inp2box2:event.target.value*price})
    }
    inp2boxChn=(event)=>{
        const {inp1,inp2}=this.state
        const price=conversion[inp1][inp2]
        this.setState=({inp1box1:event.target.value/price,
                        inp2box2:event.target.value})
        
    }
    inp1Chn=(event)=>{
        this.setState=({inp1:event.target.value})

    }
    inp2Chn=(event)=>{
        this.setState=({inp2:event.target.value})

    }
    render(){
        const {inp1,inp2,inp1box1,inp2box2}=this.state
        return(
            <div>
                <select value={inp1} onChange={this.inp1Chn}>
                    <option value="inr">Inr</option>
                    <option value="usd">Usd</option>
                    <option value="aus">Aus</option>
                    <option value="uk">UK</option>
                    <option value="riyal">Riyal</option>
                </select>
                <input type="text" value={inp1box1} onChange={this.inp1boxChn}/>
                <br></br>
                <select value={inp2} onChange={this.inp2Chn}>
                    <option value="inr">Inr</option>
                    <option value="usd">Usd</option>
                    <option value="aus">Aus</option>
                    <option value="uk">UK</option>
                    <option value="riyal">Riyal</option>
                </select>
                <input type="text" value={inp2box2} onChange={this.inp2boxChn}/>
            </div>
        )
    }
}
export default Task10;
