import React from "react";
export class Lifecycle extends React.Component{
    constructor(){
        super()
        this.state={inr:0,usd:0}
    }
    inrCh=(event)=>{
        this.setState({inr:event.target.value,usd:event.target.value*82.09})
    }
    usdCh=(event)=>{
        this.setState({usd:event.target.value,inr:event.target.value/82.09})
    }
    render(){
        return(
            <div>
            <label>ind</label>
            <input value={this.state.inr} onChange={this.inrCh}/><br></br>
            <label>usd</label>
            <input value={this.state.usd} onChange={this.usdCh}/>

            </div>
        )
    }
}
export default Lifecycle
