import React from "react";
import './task7.css'
class Child1 extends React.Component{
    constructor(props){
        super(props)
        this.state={
            expand:false
        }
        this.color='blue'
    }
    expand=()=>{
        this.setState(prev=>({expand:!prev.expand}))
        this.color=this.state.expand?'blue':'yellow'
        this.props.open(this.state.expand)
    }
    render(){
        return(
            <div className={`container ${this.color}`} onClick={this.expand}>
                <h1>{this.props.title}</h1>
                <p>{this.state.expand&&this.props.content}</p>
            </div>
        )
    }
}
export default Child1;
