import React from 'react';
import './task2.css';
class Task2 extends React.Component{
    constructor(){
        super()
        this.state={message:''}
    } 
    change(){
        this.setState({message:"Hello"})
    }
    change1(){
        this.setState({message:"Hello"})
    }
    change2(){
        this.setState({message:"Hello"})
    }
    change3(){
        this.setState({message:"Hello"})
    }
    render(){
        return(
            <div>
                <button onClick={()=>this.change()}>Item1<h1>{this.state.message}</h1></button>
                <button onClick={()=>this.change1()}>Item2<h1>{this.state.message}</h1></button>
                <button onClick={()=>this.change2()}>Item3<h1>{this.state.message}</h1></button>
                <button onClick={()=>this.change3()}>Item4<h1>{this.state.message}</h1></button>
            </div>
        )
    }
}
export default Task2;
