import React from "react";
export class Task10 extends React.Component{
    constructor(){
        super()
        this.state={inp1:"",inp2:"",inpbox1:0,inpbox2:0}
    }
    render(){
        const {inp1,inp2,inpbox1,inpbox2}=this.state
        return(
            <div>
            <select value={inp1} onChange={inp1Chn}>
                <option value="inr">Inr</option>
                <option value="usd">Usd</option>
                <option value="aus">Aus</option>
                <option value="uk">UK</option>
                <option value="riyal">Riyal</option>
            </select>
            <input type="text" value={inpbox1} onChange={inpbox1Chn}/>
            <br></br>
            <select value={inp2} onChange={inp2Chn}>
            <option value="inr">Inr</option>
            <option value="usd">Usd</option>
            <option value="aus">Aus</option>
            <option value="uk">UK</option>
            <option value="riyal">Riyal</option>
        </select>
        <input type="text" value={inpbox2} onChange={inpbox2Chn}/>
        </div>
        )
    }
}
export default Task10
