import React from "react";
export class Task10 extends React.Component{
        constructor(props){
            super(props)
            this.state={inp:""}
        }
        click1=()=>{
            this.setState({inp:this.state.inp+'1'})
        }
        click2=()=>{
            this.setState({inp:this.state.inp+'2'})
         }
        clickplus=()=>{
            this.setState({inp:this.state.inp+'+'})
        }
        clickequal=()=>{
            this.setState({inp:eval(this.state.inp)})
        }
        render(){
            return(
                <div>
                    <input type='text' value={this.state.inp}/>
                    <br></br>
                    <button onClick={this.click1}>1</button>
                    <button onClick={this.click2}>2</button>
                    <button onClick={this.clickplus}>+</button>
                    <button onClick={this.clickequal}>=</button>
                </div>
            )
        }  

}
export default Task10;
