import React from 'react'
import {useState} from 'react'
import {useAuth} from './auth'
import {useNavigate} from 'react-router-dom'
import './login.css'
const Login = () => {
    const auth=useAuth()
    const navigate=useNavigate()
    const [regdno,setRegdno]=useState('')
    const [password,setPassword]=useState('')
    const[lis,setLis]=useState(true)
    const change=(event)=>{
        setRegdno(event.target.value)
    }
    const change1=(event)=>{
      setPassword(event.target.value)
  }

    const handlelogin=(event)=>{
        console.log(auth.userlist)
        auth.userlist.map(x=>{
          if(x.regdno===regdno && x.password===password ){
            auth.login(regdno)
            navigate('/Loginsuccessfully')
            setLis(true)
          }
          else{
            setLis(false)
          }
        })
        event.preventDefault()   
        
    }
  return (
    <div class="BOX">
        <div class="login-box">
        <div class="login-header">
            <header>Login</header>
        </div>
        <div class="input-box">
            <input type="text" class="input-field" placeholder="Reg.id" autocomplete="off" required onChange={change}/>
        </div>
        <div class="input-box">
            <input type="password" class="input-field" placeholder="Password" autocomplete="off" required onChange={change1}/>
        </div>
        <div class="forgot">
            <section>
                <input type="checkbox" id="check" onChange={change}/>
                <label for="check">Remember me</label>
            </section>
            <section>
                <a href="#" class="Forgot">Forgot password</a>
            </section>
        </div>
        <div class="input-submit">
        <button class="submit-btn" id="submit" onClick={handlelogin}><label for="submit">Sign In</label></button>
        
        {!lis?<p>Invalid user and password</p>:''}
        </div>
        <div class="sign-up-link">
            <p>Don't have account? <a href="/signup">Sign Up</a></p>
        </div>
    </div>
      </div>
  )
}

export default Login
