import React,{useState} from "react";
import { useAuth } from "./auth";
import './singup.css'
import { useNavigate } from "react-router-dom";
const Signup =() => {
    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const [firstName,setFirstName]=useState('')
    const[lastName,setLastName]=useState('')
    const[userBool,setUserBool]=useState(false)
    const auth =useAuth()
    const navigate = useNavigate()
    const handlesignup = (event) => {
        const userTaken=auth.userlist.some(x=>x.email===email)
        if(userTaken){
            setUserBool(true)
        }
        else{
        auth.signup(email,password,firstName,lastName)
        navigate('/Signupsuccessfully')
        }
        event.preventDefault()
    
}
    const change =(event)=>{
        setEmail(event.target.value)
    }
    const change1 =(event)=>{
        setPassword(event.target.value)
    }
    return(
        <div>
            <div className="body1">
            <div className="signupbox">
            <form onSubmit={handlesignup}>
                <div class="input-box1">
            <input  class="input-field" placeholder="First name" autocomplete="off"/> 
            </div>
            <br></br>
            <div class="input-box1">
            <input class="input-field" placeholder="Last name" autocomplete="off"/> 
            </div>
            <br></br>
            <div class="input-box1">
            <input type='text' value={email} class="input-field" placeholder="User name" autocomplete="off" onChange={change} required/> 
            </div>
            <br></br>
            <div class="input-box1">
            <input type='password' value={password}  class="input-field" placeholder="Password" autocomplete="off" onChange={change1} required/> 
            </div>
            <br></br>
            <div className="input-box1">
            <button type='submit' className="input-field">Signup</button>
            </div>
            </form>
            </div>
            
        </div>
            
        </div>
    )
}
export default Signup
