import React from 'react'
import {useState} from 'react'
import {useAuth} from './auth'
import {useNavigate} from 'react-router-dom'
const Login = () => {
    const auth=useAuth()
    const navigate=useNavigate()
    const [email,setEmail]=useState('')
    const [password,setPassword]=useState('')
    const[lis,setLis]=useState(true)
    const change=(event)=>{
        setEmail(event.target.value)
    }
    const change1=(event)=>{
      setPassword(event.target.value)
  }

    const handlelogin=(event)=>{
        console.log(auth.userlist)
        auth.userlist.map(x=>{
          if(x.email===email && x.password===password){
            auth.login(email)
            navigate('/Loginsuccessfully')
            setLis(true)
          }
          else{
            setLis(false)
          }
        })
        event.preventDefault()   
        
    }
  return (
    <div>
      <form onSubmit={handlelogin} className='login-form'>
      <label>Email</label>
      <input type='email' value={email} onChange={change} required/>
      <br></br>
      <label>password</label>
      <input type='password' value={password} onChange={change1} required/>
      <br></br>
      <button type='submit'>Login</button>
      {!lis?<p>Invalid user and password</p>:''}
      <h4>if you don't have account</h4>
      <a href='/signup'>Signup</a>
      </form>  
      <br></br>
    </div>
  )
}

export default Login
